package uber._267_Palindrome_Permutation_II;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.HashSet;
import java.util.List;
import java.util.Set;
import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * @author jacka
 * @version 1.0 on 1/8/2020
 */
class SolutionITest {

  @ParameterizedTest
  @MethodSource("solutionStream")
  public void testEvenDup(Solution solution) {
    final String s = "aabb";
    final Set<String> expected = new HashSet<>();
    expected.add("abba");
    expected.add("baab");
    final List<String> actual = solution.generatePalindromes(s);
    assertEquals(expected.size(), actual.size());
    assertEquals(expected, new HashSet<>(actual));
  }

  @ParameterizedTest
  @MethodSource("solutionStream")
  public void testOddDup(Solution solution) {
    final String s = "aaabaaa";
    final Set<String> expected = new HashSet<>();
    expected.add("aaabaaa");
    final List<String> actual = solution.generatePalindromes(s);
    assertEquals(expected.size(), actual.size());
    assertEquals(expected, new HashSet<>(actual));
  }

  @ParameterizedTest
  @MethodSource("solutionStream")
  public void testAaa(Solution solution) {
    final String s = "aaa";
    final List<String> expected = List.of("aaa"),
        actual = solution.generatePalindromes(s);
    assertEquals(expected, actual);
  }

  @ParameterizedTest
  @MethodSource("solutionStream")
  public void testTLE(Solution solution) {
    final String s =
        "";
    long startTime = System.nanoTime();
    solution.generatePalindromes(s);
    long endTime = System.nanoTime();
    System.out.println((endTime - startTime) / 1E3);
  }

  static Stream<Solution> solutionStream() {
    return Stream.of(
        new SolutionI()
    );
  }
}