package _1001_1050._1044_Longest_Duplicate_Substring;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * @author jacka
 * @version 1.0 on 12/25/2019
 */
class SolutionTest {

  @ParameterizedTest
  @MethodSource("solutionProvider")
  void testOnlineCase1(Solution solution) {
    final String input = "banana", expected = "ana",
        actual = solution.longestDupSubstring(input);
    assertEquals(expected, actual);
  }

  @ParameterizedTest
  @MethodSource("solutionProvider")
  void testOnlineCase2(Solution solution) {
    final String input = "abcd", expected = "",
        actual = solution.longestDupSubstring(input);
    assertEquals(expected, actual);
  }

  @ParameterizedTest
  @MethodSource("solutionProvider")
  void testOnlineCaseMLE3(Solution solution) {
    final String input =
        "",
        expected = "akyj",
        actual = solution.longestDupSubstring(input);
    assertEquals(expected, actual);
  }

  @ParameterizedTest
  @MethodSource("solutionProvider")
  void testOnlineCaseTLE(Solution solution) {
    final String input =
        "",
        expected = "maoli",
        actual = solution.longestDupSubstring(input);
    assertEquals(expected, actual);
  }

  @ParameterizedTest
  @MethodSource("solutionProvider")
  void testFailedCase1(Solution solution) {
    final String input =
        "",
        expected = "aeeebaabd",
        actual = solution.longestDupSubstring(input);
    assertEquals(expected, actual);
  }

  static Stream<Solution> solutionProvider() {
    return Stream.of(
        // new TrieSolution() // will time out
        new RabinKarpSolution()
    );
  }
}